import { jobLogList, jobLogInfo } from "@/api/sys";
export default {
  data() {
    return {
      visible: false,
      dataForm: {
        id: ""
      },
      dataList: [],
      pageIndex: 1,
      pageSize: 10,
      totalPage: 0,
      dataListLoading: false
    };
  },
  methods: {
    init() {
      this.visible = true;
      this.getDataList();
    },
    // 获取数据列表
    async getDataList() {
      this.dataListLoading = true;
      const params = {
        page: this.pageIndex,
        limit: this.pageSize,
        jobId: this.dataForm.id
      };
      const { data } = await jobLogList(params);
      if (data && data.code === 0) {
        this.dataList = data.page.list;
        this.totalPage = data.page.totalCount;
      } else {
        this.dataList = [];
        this.totalPage = 0;
      }
      this.dataListLoading = false;
      // this.$http({
      //   url: this.$http.adornUrl("/sys/scheduleLog/list"),
      //   method: "get",
      //   params: this.$http.adornParams({
      //     page: this.pageIndex,
      //     limit: this.pageSize,
      //     jobId: this.dataForm.id
      //   })
      // }).then(({ data }) => {
      //   if (data && data.code === 0) {
      //     this.dataList = data.page.list;
      //     this.totalPage = data.page.totalCount;
      //   } else {
      //     this.dataList = [];
      //     this.totalPage = 0;
      //   }
      //   this.dataListLoading = false;
      // });
    },
    // 每页数
    sizeChangeHandle(val) {
      this.pageSize = val;
      this.pageIndex = 1;
      this.getDataList();
    },
    // 当前页
    currentChangeHandle(val) {
      this.pageIndex = val;
      this.getDataList();
    },
    // 失败信息
    async showErrorInfo(id) {
      const { data } = await jobLogInfo(`/sys/scheduleLog/info/${id}`);
      if (data && data.code === 0) {
        this.$alert(data.log.error);
      } else {
        this.$message.error(data.msg);
      }
      // this.$http({
      //   url: this.$http.adornUrl(`/sys/scheduleLog/info/${id}`),
