import AddOrUpdate from "./components/schedule-add-or-update";
import Log from "./components/schedule-log";
import { jobList, jobDelete, jobPause, jobResume, jobRun } from "@/api/sys";
export default {
  data() {
    return {
      dataForm: {
        beanName: ""
      },
      dataList: [],
      pageIndex: 1,
      pageSize: 10,
      totalPage: 0,
      dataListLoading: false,
      dataListSelections: [],
      addOrUpdateVisible: false,
      logVisible: false
    };
  },
  components: {
    AddOrUpdate,
    Log
  },
  mounted() {
    this.getDataList();
  },
  methods: {
    // 查询清空
    reset(dataForm) {
      // this.$refs[dataForm].resetFields();
      this.dataForm.beanName="";
      this.restPage();
    },
    // 跳到第一页进行查询
    restPage() {
      this.pageIndex = 1;
      this.getDataList();
    },
    // 获取数据列表
    async getDataList() {
      this.dataListLoading = true;
      const params = {
        page: this.pageIndex,
        limit: this.pageSize,
        beanName: this.dataForm.beanName
      };
      const { data } = await jobList(params);
      if (data && data.code === 0) {
        this.dataList = data.page.list;
        this.totalPage = data.page.totalCount;
      } else {
        this.dataList = [];
        this.totalPage = 0;
      }
      this.dataListLoading = false;

      // this.$http({
      //   url: this.$http.adornUrl("/sys/schedule/list"),
      //   method: "get",
      //   params: this.$http.adornParams({
